mapper.select:
select count(*)
from products
where product_category = #{category}

select thumbnail
from products
where product_id = #{id}

select thumbnailhover
from products
where product_id = #{id}

select rno, product_id, product_name, product_category, product_price, product_desc,selling_price, percentage_discount, available_quantity, post_date, thumbnail, main_img, sub1_img, sub2_img, sub3_img, thumbnailhover from( select rownum as rno, product_id, product_name, product_category, product_price, product_desc,selling_price, percentage_discount, available_quantity, post_date, thumbnail, main_img, sub1_img, sub2_img, sub3_img, thumbnailhover from ( select product_id, product_name, product_category, product_price, product_desc,selling_price, percentage_discount, available_quantity, post_date, thumbnail, main_img, sub1_img, sub2_img, sub3_img, thumbnailhover from products
where product_category = #{category}
order by post_date asc
order by product_name asc
order by selling_price desc
order by selling_price asc
) where rownum <= #{endRowNo} ) where rno >= #{startRowNo}
